(function(){tinymce.PluginManager.requireLangPack('cmspage');tinymce.create('tinymce.plugins.CMSpage',{init:function(ed,url){ed.addCommand('mceCMSpage',function(){ed.windowManager.open({file:url+'/dialog.htm',width:500+parseInt(ed.getLang('cmspage.delta_width',0)),height:400+parseInt(ed.getLang('cmspage.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('cmspage',{title:'cmspage.desc',cmd:'mceCMSpage',image:url+'/img/search_page.png'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('cmspage',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'CMSpage plugin',author:'Gerits Aurelien',authorurl:'http://www.magix-cms.com',infourl:'http://www.magix-cms.com',version:"alpha 0.1"};}});tinymce.PluginManager.add('cmspage',tinymce.plugins.CMSpage);})();