(function(){tinymce.PluginManager.requireLangPack('news_search');tinymce.create('tinymce.plugins.NewsSearch',{init:function(ed,url){ed.addCommand('mceNewsSearch',function(){ed.windowManager.open({file:url+'/dialog.htm',width:500+parseInt(ed.getLang('news_search.delta_width',0)),height:400+parseInt(ed.getLang('news_search.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('news_search',{title:'news_search.desc',cmd:'mceNewsSearch',image:url+'/img/search_news.png'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('news_search',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'Newssearch plugin',author:'Gerits Aurelien',authorurl:'http://www.magix-cms.com',infourl:'http://www.magix-cms.com',version:"alpha 0.1"};}});tinymce.PluginManager.add('news_search',tinymce.plugins.NewsSearch);})(); 